#include <stdio.h>
#define MIN(a, b) (((a)<(b))?(a):(b))
#define INF (30000)

int answer(int *A_arr, int *B_arr, int arrsize, int target) {
	int num_ta_in_A = 0;
	int num_ta_in_B = 0;
	int i;

	
	for (i = 0; i < arrsize; i++) {
		if (A_arr[i] != target && B_arr[i] != target) {
			return INF;
		} else if (A_arr[i] == target && B_arr[i] == target) {
			continue;
		} else if (A_arr[i] == target) {
			num_ta_in_A++;
		} else if (B_arr[i] == target) {
			num_ta_in_B++;
		}
	}
	return MIN(num_ta_in_A, num_ta_in_B);
}

int minDominoRotations(int* A, int ASize, int* B, int BSize){
	int retAnswer = INF;
	int got;
	
	got = answer(A, B, ASize, A[0]);	
	retAnswer = MIN(retAnswer, got);	
	if (B[0] != A[0]) {
		got = answer(A, B, ASize, B[0]);	
		retAnswer = MIN(retAnswer, got);
	}
	
	if (retAnswer == INF) {
		return -1;
	} else {
		return retAnswer;
	}
}

